import { Component, OnInit } from '@angular/core';
import { ListItemsService } from './service/list-items.service';
import { ListItemsModel } from './model/list-items.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.sass']
})
export class AppComponent implements OnInit {
  arrayItems!: ListItemsModel[];
  listItems!: ListItemsModel;

  constructor( public listItemsService: ListItemsService ) { }

  ngOnInit(): void {
    this.gettingListItems();
  }

  gettingListItems() {
    this.listItemsService.getList().subscribe(items => {
      this.arrayItems = items;
       console.log(this.arrayItems);

    });
  }

  updatingItem(id: any, name: any, value: boolean) {
    this.listItemsService.updateItem(id, name, value);
  }

  // this.arrayItems.forEach(data => {
  //   this.listItems = data;
  //   console.log("lista de items: ", this.listItems);
  // });
}
<section id="body">

  <div class="content">
    <div class="title">
      <h1>Camila e Daniel</h1>
    </div>

    <div class="description">
      <p>Lista de presentes que gostariamos de ganhar!</p>
    </div>

    <div class="to-do-list" *ngFor="let item of arrayItems">
      <div class="card-item">
        <!-- <input type="checkbox" (click)="updatingItem(item._id, item.name, item.value)"> -->

        <div class="name-item">
          <span>{{ item.name }}</span>
        </div>
      </div>
    </div>
  </div>

</section>
