import { Component } from '@angular/core';
import { ListItemsService } from './service/list-items.service';
import { ListItemsModel } from './model/list-items.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.sass']
})
export class AppComponent {
  listItems: ListItemsModel = {};

  constructor(public listItemsService: ListItemsService) {
    this.gettingListItems();
  }

  gettingListItems() {
    this.listItemsService.getList()
    .subscribe(items => {
      const test = items;
      items.forEach(data => {
        this.listItems = data
        console.log('items: ', data);
      });
    });
  }

  updatingItem(id: string, name: string, value: boolean) {
    this.listItemsService.updateItem(id, name, value);
  }
}
<section id="body">

  <div class="content">
    <div class="title">
      <h1>Camila e Daniel</h1>
    </div>

    <div class="description">
      <p>Lista de presentes que gostariamos de ganhar!</p>
    </div>

    <div class="to-do-list" *ngFor="let item of listItems">
      <div class="card-item">
        <input type="checkbox" >

        <div class="name-item">
          <span>{{ item }}</span>
        </div>
      </div>
    </div>
  </div>

</section>
