import { Component } from '@angular/core';
import { ListItemsService } from './service/list-items.service';
import { ListItemsModel } from './model/list-items.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.sass']
})
export class AppComponent {
  arrayItems: [] = [];

  constructor(
    public listItemsService: ListItemsService,
    public listItems: ListItemsModel
  ) {
    // this.listItems = this.gettingListItems();
    this.gettingListItems();

    console.log(ListItemsModel);

  }

  async gettingListItems() {
    this.listItemsService.getList()
    .subscribe(items => {
      this.arrayItems = items;
    });

    // this.arrayItem.forEach(data => {
    //   data;
    // });

    return this.arrayItems;
  }

  updatingItem(id: string, name: string, value: boolean) {
    this.listItemsService.updateItem(id, name, value);
  }
}
