      <div class="row">
        <div class="col-2 col-icon">
          <i class="bi bi-toggle-off icon" *ngIf="item.value == false"></i>
          <i class="bi bi-toggle-on icon" *ngIf="item.value == true"></i>
        </div>
        <div class="col-8">
          <span>{{ item.name }}</span>
        </div>
