import { Component, OnInit } from '@angular/core';
import { ListItemsService } from './service/list-items.service';
import { ListItemsModel } from './model/list-items.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.sass']
})
export class AppComponent implements OnInit {
  itemsKitchen!: ListItemsModel[];
  itemsRoom!: ListItemsModel[];
  itemsServiceArea!: ListItemsModel[];
  itemsBathroom!: ListItemsModel[];

  constructor( public listItemsService: ListItemsService ) { }

  ngOnInit(): void {
    this.getAllLists();
  }

  getAllLists(): void {
    this.gettingListItemsKitchen();
    this.gettingListItemsRoom();
    this.gettingListItemsServiceArea();
    this.gettingListItemsBathroom();
  }

  gettingListItemsKitchen(): void {
    this.listItemsService.getListKitchen().subscribe(items => {
      this.itemsKitchen = items;
    });
  }

  gettingListItemsRoom(): void {
    this.listItemsService.getListRoom().subscribe(items => {
      this.itemsRoom = items;
    });
  }

  gettingListItemsServiceArea(): void {
    this.listItemsService.getListServiceArea().subscribe(items => {
      this.itemsServiceArea = items;
    });
  }

  gettingListItemsBathroom(): void {
    this.listItemsService.getListBathroom().subscribe(items => {
      this.itemsBathroom = items;
    });
  }

  updatingItem(id: any, value: any): void {
    if (value == true) {
      this.listItemsService.updateItem(id, value = false);
    } else {
      this.listItemsService.updateItem(id, value = true);
    }

    this.getAllLists();
  }
}
<section id="body">
  <div class="title">
    <h1>Camila e Daniel</h1>
  </div>

  <div class="description">
    <p>Lista de presentes que gostariamos de ganhar!</p>
  </div>

  <div class="category kitchen-category">
    <h3>Cozinha</h3>
  </div>

  <div class="content content-kitchen">
    <div class="to-do-list mb-4" *ngFor="let item of itemsKitchen" (click)="updatingItem(item._id, item.value)">
      <i class="bi bi-toggle-off mx-3" *ngIf="item.value == false"></i>
      <i class="bi bi-toggle-on mx-3" *ngIf="item.value == true"></i>
      <span>{{ item.name }}</span>
    </div>
  </div>

  <div class="category room-category">
    <h3>Quarto</h3>
  </div>

  <div class="content content-room">
    <div class="to-do-list mb-4" *ngFor="let item of itemsRoom" (click)="updatingItem(item._id, item.value)">
      <i class="bi bi-toggle-off" *ngIf="item.value == false"></i>
      <i class="bi bi-toggle-on" *ngIf="item.value == true"></i>
      <label for="name">{{ item.name }}</label>
    </div>
  </div>

  <div class="category service-area-category">
    <h3>Área de Serviço</h3>
  </div>

  <div class="content content-service-area">
    <div class="to-do-list mb-4" *ngFor="let item of itemsServiceArea" (click)="updatingItem(item._id, item.value)">
      <i class="bi bi-toggle-off" *ngIf="item.value == false"></i>
      <i class="bi bi-toggle-on" *ngIf="item.value == true"></i>
      <label for="name">{{ item.name }}</label>
    </div>
  </div>

  <div class="category bathroom-category">
    <h3>Banheiro</h3>
  </div>

  <div class="content content-bathroom">
    <div class="to-do-list mb-4" *ngFor="let item of itemsBathroom" (click)="updatingItem(item._id, item.value)">
      <i class="bi bi-toggle-off" *ngIf="item.value == false"></i>
      <i class="bi bi-toggle-on" *ngIf="item.value == true"></i>
      <label for="name">{{ item.name }}</label>
    </div>
  </div>
</section>
