import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class ListItemsService {
  apiUrl: string = 'http://localhost:3334/items';

  httpOptions: {} = {
    Headers: new HttpHeaders({
      'Content-Type': 'application/json'
    })
  };

  constructor(private http : HttpClient) { }

  async getList(): Promise<{}> {
    let list = {};

    this.http.get('http://localhost:3334/items').subscribe(data => {
      if (data != null) {
        list = data;
      } else {
        alert("Os dados da lista estão retornando vazio!")
      }
    });

    return list;
  }

  updateItem() {
    console.log("teste da função update no serviço");
  }
}
