import { Component } from '@angular/core';
import { ListItemsService } from './service/list-items.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.sass']
})
export class AppComponent {
  items!: {}
  constructor(public listItemsService: ListItemsService) {
    this.items = this.gettingListItems();
    this.updatingItem();
  }

  async gettingListItems(): Promise<{}> {
    const listItems = await this.listItemsService.getList();

    console.log('items: ', listItems);

    return listItems;
  }

  updatingItem() {
    this.listItemsService.updateItem();
  }
}
<section id="body">

  <div class="content">
    <div class="title">
      <h1>Camila e Daniel</h1>
    </div>

    <div class="description">
      <p>Lista de presentes que gostariamos de ganhar!</p>
    </div>

    <div class="to-do-list">
      <div class="card-item" *ngFor="let item of items">
      <!-- <div class="card-item"> -->
        <input type="checkbox" >

        <div class="name-item">
          <span>item</span>
          <span>{{ item.name }}</span>
        </div>
      </div>
    </div>
  </div>

</section>
