        </div>
        <div class="col-2">
          <i class="bi bi-gift" *ngIf="item.value == false"></i>
          <i class="bi bi-gift gift" *ngIf="item.value == true"></i>
        </div>
      </div>
    </div>
  </div>

  <div class="category bathroom-category">
    <h3>Banheiro</h3>
  </div>

  <div class="content content-bathroom">
    <div class="to-do-list mb-4" *ngFor="let item of itemsBathroom" (click)="updatingItem(item._id, item.value)" [ngClass]="item.value == true ? toDoListChecked : null">
      <div class="row">
        <div class="col-2 col-icon">
          <i class="bi bi-toggle-off icon" *ngIf="item.value == false"></i>
          <i class="bi bi-toggle-on icon" *ngIf="item.value == true"></i>
        </div>
        <div class="col-8">
          <span *ngIf="item.value == false">{{ item.name }}</span>
          <span class="name-selected" *ngIf="item.value == true"><del>{{ item.name }}</del></span>
        </div>
        <div class="col-2">
          <i class="bi bi-gift" *ngIf="item.value == false"></i>
          <i class="bi bi-gift gift" *ngIf="item.value == true"></i>
        </div>
      </div>
    </div>
  </div>
</section>

